import React from "react";
import penPath from "../images/pen.svg"
import api from "../utils/Api.js"

function Main(props) {
  const [userName, setUserName] = React.useState("");
  const [userDescription, setUserDescription] = React.useState("");
  const [userAvatar, setUserAvatar] = React.useState("");
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getUser()
      .then(res => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar);
      });
    }, []);

  return (
    <main>
      <section className="profile">
        <div className="profile__avatar-wrapper">
          <div className="profile__avatar-overlay" onClick={props.onEditAvatar}>
            <img src={penPath} alt="" className="profile__avatar-overlay-image"/>
          </div>
          <img className="profile__avatar" src={userAvatar} alt="аватар"/>
        </div>
        <div className="profile__info">
          <div className="profile__info-wrapper">
            <h1 className="profile__info-name">{userName}</h1>
              <button className="profile__info-edit" type="button" onClick={props.onEditProfile}></button>
          </div>
          <p className="profile__info-bio">{userDescription}</p>
        </div>
        <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
      </section>

      <section>
        <ul className="elements">
        {comments.map((message, i) => (
          // Важный атрибут: key
          <div key={message.id}>
            <h3>{message.author}</h3>
            <div>{message.text}</div>
          </div>
        ))}
          <template id="element">
              <li className="element">
                <div className="element__image-wrapper">
                  <button type="button" className="element__trash"></button>
                  <img className="element__image" alt="" src="#" />
                </div>
                <div className="element__wrapper">
                  <h3 className="element__text"></h3>
                  <div className="element__likes-wrapper">
                    <button type="button" className="element__heart"></button>
                    <p className="element__likecount">0</p>
                  </div>

                </div>
              </li>
            </template>
        </ul>
      </section>

    </main>
  )
}

